import React from 'react';
import styled from 'styled-components';
import {AiOutlineSearch, AiOutlineEllipsis} from 'react-icons/ai'

function TopHeader (props) {

    const {} = props;

    return (
        <Container>
            <Logo>
                <img src="" alt="unsplash"/>
            </Logo>
            <SearchForm>
                <AiOutlineSearch Color={"#333"} fontSize={"20"}/>
                <input type="text"/>
            </SearchForm>
            <QuickMenu>
                <SubMenu>
                    <Link>Topics</Link>
                    <Link>Explore</Link>
                    <Link>
                        <AiOutlineEllipsis/>
                    </Link>
                    <Link className={"btn"}>Submit a photo</Link>
                </SubMenu>
                <UserInfo>
                    <Link>Login</Link>
                    <Link className={"btn_mint"}>Join Free</Link>
                </UserInfo>

            </QuickMenu>
        </Container>
    )
}

const Container = styled.div`
  width: 100%;
  position:fixed;
  display:flex;
  align-items:center;
  justify-content: space-between;
  background: #fff;
  box-shadow: 0px 0px 5px 10px rgba(0,0,0,.05);
  padding: 10px 0;
`
const Logo = styled.div`

`;
const SearchForm = styled.div`
  display:flex;
  align-items:center;
  justify-content:flex-start;
  flex: 1;
  background: #eee;
  -webkit-border-radius: 60px;-moz-border-radius: 60px;border-radius: 60px;
  AiOutlineSearch {

  }
  input {
    display:flex;
    border:none;
    background:none;
  }
`;
const QuickMenu = styled.div`
    display:flex;
    align-items:center;
    justify-content:center;

`;
const SubMenu = styled.div`
    display:flex;
    align-items:center;
`;
const UserInfo = styled.div`
    display:flex;
    justify-content:center;
    align-items:center;
    &:before {
        content: '';
        position: absolute;
        background: #aaa;
        width: 2px;
        height: 100%;
      }
`;
const Link = styled.div`
    color: #666;
    font-size: 16px;
    font-weight: 500;
    &.btn {
      position:relative;
      border: 1px solid #666;
      -webkit-border-radius: 5px;-moz-border-radius: 5px;border-radius: 5px;
      padding: 8px 10px;
    }
    &.btn_mint {
      background: #3cb46e;
      color: #fff;
      -webkit-border-radius: 5px;-moz-border-radius: 5px;border-radius: 5px;
      padding: 8px 10px;
      &:hover {
        background: #37a866;
      }
    }

`;
const ButtonSubmit = styled.div`

`;
export default TopHeader;
